INSERT INTO public.faqs (question, answer, category, display_order, is_active) VALUES
('Do you offer discounts for long-distance travel?', 'Yes. We provide an automatic 10% discount on same-day return journeys of 200 miles or more. The discount is applied during booking.', 'Pricing', 1, true),
('How is mileage calculated?', 'Mileage is calculated using the most efficient route between your pickup and destination. Return mileage is included if selected.', 'Pricing', 2, true),
('Does the discount apply to additional charges?', 'No. The discount applies to the mileage rate only. Additional costs such as waiting time, parking, tolls, or extra stops are charged separately.', 'Pricing', 3, true),
('What counts as a same-day return?', 'A journey where both outbound and return travel are completed on the same calendar day, without overnight stays.', 'Pricing', 4, true),
('Will I see the discount before I pay?', 'Absolutely. Your discounted price is shown clearly in the booking summary before you confirm your reservation.', 'Pricing', 5, true);
